class TaskTile extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return CheckboxListTile(
      value: false,
      onChanged: null,
      title: Text(
        'Buy tea',
        style: TextStyle(color: Colors.black),
      ),
    );
  }
}
